import React, { Component } from 'react';
import IconWrapper from '../styles/IconWrapper';
import MicSvg from '../styles/MicSvg';
import MuteSvg from '../styles/MuteSvg';
import ListenSvg from '../styles/ListenSvg';
import { hasCustomRenderer as hcr, getComponent as gc } from '../utils/helper';

const STATUS = {
  inactive: 'INACTIVE',
  stopped: 'STOPPED',
  active: 'ACTIVE',
  denied: 'DENIED'
};

const Icon = props => {
  const { status, ...rest } = props;
  let url;
  if (!window.SpeechRecognition) {
    url =
      'https://cdn3.iconfinder.com/data/icons/glypho-music-and-sound/64/microphone-off-512.png';
  }

  switch (status) {
			case STATUS.active:
				return <ListenSvg {...rest} />;
			case STATUS.stopped:
			case STATUS.denied:
				return <MuteSvg {...rest}  />;
			default:
				return <MicSvg {...rest}  />;
		}

};

class Mic extends Component {
  shouldComponentUpdate(nextProps, nextState, nextContext) {
    return (
      nextProps.iconPosition !== this.props.iconPosition ||
      nextProps.className !== this.props.className ||
      nextProps.status !== this.props.status ||
      nextProps.applyClearStyle !== this.props.applyClearStyle ||
      nextProps.showIcon !== this.props.showIcon
    );
  }

  render() {
    const {
      iconPosition,
      className,
      onClick,
      status,
      applyClearStyle,
      showIcon
    } = this.props;

    const getComponent = () => {
      const data = {
        onClick,
        status
      };
      return gc(data, this.props);
    };

    const hasCustomRenderer = hcr(this.props);

    return (
      <IconWrapper
      >
        {hasCustomRenderer ? (
          getComponent()
        ) : (
          <Icon className={className} onClick={onClick} status={status} />
        )}
      </IconWrapper>
    );
  }
}

export default Mic;
import React from 'react';
import CancelSvg from '../styles/CancelSvg';
import IconGroup from '../styles/IconGroup';
import IconWrapper from '../styles/IconWrapper';
import SearchSvg from '../styles/SearchSvg';
import { getClassName } from '../utils/helper';
import Mic from './Mic';

const SearchIcon = props => {
  if (props.showIcon) {
    return props.icon || <SearchSvg />;
  }
  return null;
};

const Icons = props => {
  const {
    clearValue,
    iconPosition,
    showClear,
    clearIcon,
    currentValue,
    handleSearchIconClick,
    showIcon,
    icon,
    enableVoiceSearch,
    innerClass,
    renderMic,
    getMicInstance,
    onMicClick,
    micStatus
  } = props;

  return (
    <div>
      <IconGroup groupPosition="right" positionType="absolute">
        {currentValue && showClear && (
          <IconWrapper onClick={clearValue}>
            {clearIcon || <CancelSvg />}
          </IconWrapper>
        )}
        {enableVoiceSearch && (
          <Mic
            getInstance={getMicInstance}
            render={renderMic}
            className={getClassName(innerClass, 'mic') || null}
            onClick={onMicClick}
            status={micStatus}
          />
        )}
        {iconPosition === 'right' && (
          <IconWrapper onClick={handleSearchIconClick}>
            <SearchIcon showIcon={showIcon} icon={icon} />
          </IconWrapper>
        )}
      </IconGroup>

      <IconGroup groupPosition="left" positionType="absolute">
        {iconPosition === 'left' && (
          <IconWrapper onClick={handleSearchIconClick}>
            <SearchIcon showIcon={showIcon} icon={icon} />
          </IconWrapper>
        )}
      </IconGroup>
    </div>
  );
};

export default Icons;
import React from 'react';

const SuggestionItem = ({ currentValue, suggestion }) => {
  const { label, value, isPredictiveSuggestion } = suggestion;

  const stringToReplace = currentValue.split(' ').join('|');
  // label has highest precedence
  if (label) {
    if (typeof label === 'string') {
      try {
        return (
          <div
            className="trim"
            dangerouslySetInnerHTML={{
              __html: isPredictiveSuggestion
                ? label
                : label.replace(new RegExp(stringToReplace, 'ig'), matched => {
                    return `<mark class="highlight-class">${matched}</mark>`;
                  })
            }}
          />
        );
      } catch (e) {
        return label;
      }
    }
    return label;
  }
  return value;
};

export default SuggestionItem;
